sys_script.script:
(function executeRule(current, previous /*null when async*/) {

try { 
 var r = new sn_ws.RESTMessageV2('ABC', 'Post');
r.setStringParameterNoEscape('subcat', current.sub_category);
 r.setStringParameterNoEscape('cat', current.category);
 r.setStringParameterNoEscape('description', current.description);
 r.setStringParameterNoEscape('ci', current.cmdb_ci);
 r.setStringParameterNoEscape('channel', current.contact_type);
 r.setStringParameterNoEscape('urgency', current.urgency);
 r.setStringParameterNoEscape('priority', current.priority);
 r.setStringParameterNoEscape('shortdescription', current.short_description);
 r.setStringParameterNoEscape('impact', current.impact);
r.setStringParameterNoEscape('caller', current.caller_id);

 var response = r.execute();
 var responseBody = response.getBody();
 var httpStatus = response.getStatusCode();
}
catch(ex) {
 var message = ex.message;
}
})(current, previous);
